import { useState } from 'react';
import '../app.css'
import axios from 'axios';




function LoginPage() {

  const [login_info, setlogin_info] = useState({  
    Name: '',
    Surname: '',
    Email: '',
    Password: '',});

  const handleChange = (event)=>{
    const { name, value } = event.target;
    setlogin_info((prevInfo) => ({ ...prevInfo, [name]: value }));
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    const result = await login();
    console.log(result)
    alert (login_info);
  }

  async function login() {
  try {
    const response = await axios.post('/login/Landlord', login_info);
    return response
  } catch (error) {
    console.log(error);
  }
}



  return (

    <form className='Form'>

    <input type="text" placeholder="your@email.com" name='Name' value={login_info.Name} onChange={handleChange}/>


      <label>Enter your surname:
        <input type="text" name='Surname' value={login_info.Surname } onChange={handleChange}/>
      </label>

      <label>Enter your email:
        <input type="email" name='Email' value={login_info.Email} onChange={handleChange}/>
      </label>

      <label>Enter your password:
        <input type="text" name ='Password' value={login_info.Password} onChange={handleChange}/>
      </label>
      <button type="submit" className = 'Submit-btn'onClick={handleSubmit}/>
    </form>

  );
}

export default LoginPage;
