import { useState } from 'react';
import '../app.css'
import axios from 'axios';




function LoginPage() {

  const [selectedOption, setSelected_option] = useState('S');

  const handleOption_change = (event)=>{
    setSelected_option(event.target.value);

  }



  const [login_info, setlogin_info] = useState({  
    Name: '',
    Surname: '',
    Email: '',
    Password: '',});

  const handleChange = (event)=>{
    const { name, value } = event.target;
    setlogin_info((prevInfo) => ({ ...prevInfo, [name]: value }));
  }

  const handleSubmit = async (event) => {
    console.log(login_info)
    event.preventDefault();
    const result = await login();
    console.log(result)
    alert (login_info);
  }

  async function login() {
  try {
    const response = await axios.post('/login/Landlord', login_info);
    return response
  } catch (error) {
    console.log(error);
  }
}



  return (
  <div className='LoginPage'>

    <h1>Login</h1>

    <p>Please select the type of user:</p>
    <div className='User-Selector'>

      <div className='selector-input'>
        <input type="radio" id="Landlord" name="user" value='L'  checked={selectedOption === 'L'} onChange={handleOption_change}/>
        <label htmlFor="Landlord">Landlord</label>
      </div>
      <div className='selector-input'>
        <input type="radio" id="Performer" name="user" value='P'  checked={selectedOption === 'P'} onChange={handleOption_change}/>
        <label htmlFor="Performer">Performer</label>
      </div>
      <div className='selector-input'>
        <input type="radio" id="Spectator" name="user" value='S'  checked={selectedOption === 'S'} onChange={handleOption_change}/>
        <label htmlFor="Spectator">Spectator</label>
      </div>
    </div>

    {
  (() => {
    switch (selectedOption) {
      case 'L':
        return (
          <form className='Form'>
            <input className='login-element' type="email" placeholder="your@email.com" name='Email' value={login_info.Email} onChange={handleChange} />
            <input className='login-element' type="text" placeholder="Password" name='Password' value={login_info.Password} onChange={handleChange} />
            <button className='login-element' type="submit" onClick={handleSubmit}>Login</button>
          </form>
        );
      case 'S':
        return(
          <div>
            Zdravo
          </div>
        )
      // Add other cases for different options if needed
      default:
        return null;
    }
  })()
}


  </div>
  );
}

export default LoginPage;
