import { useState } from 'react';
import '../app.css'
import axios from 'axios';




function LoginPage() {

  const [user_type, setuser_type] = useState("");
  const handleUser_type = (event)=>{
    const { name, value } = event.target;
    setuser_type((prevInfo) => ({ ...prevInfo, [name]: value }));
  }



  const [login_info, setlogin_info] = useState({  
    Name: '',
    Surname: '',
    Email: '',
    Password: '',});

  const handleChange = (event)=>{
    const { name, value } = event.target;
    setlogin_info((prevInfo) => ({ ...prevInfo, [name]: value }));
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    const result = await login();
    console.log(result)
    alert (login_info);
  }

  async function login() {
  try {
    const response = await axios.post('/login/Landlord', login_info);
    return response
  } catch (error) {
    console.log(error);
  }
}



  return (
  <div className='LoginPage'>

    <h1>Login</h1>

    <p>Please select the type of user:</p>
    <div className='User-Selector'>

      <div className='selector-input'>
        <input type="radio" id="Landlord" name="user" value="L"/>
        <label for="Landlord">Landlord</label>
      </div>
      <div className='selector-input'>
        <input type="radio" id="Performer" name="user" value="P"/>
        <label for="Performer">Performer</label>
      </div>
      <div className='selector-input'>
        <input type="radio" id="Spectator" name="user" value="S"/>
        <label for="Spectator">Spectator</label>
      </div>
    </div>

    <form className='Form'>
      <input className='login-element' type="text" placeholder="your@email.com" name='Name' value={login_info.Name} onChange={handleChange}/>

      <input className='login-element' type="text" name='Surname' value={login_info.Surname } onChange={handleChange}/>

      <input className='login-element' type="email" name='Email' value={login_info.Email} onChange={handleChange}/>

      <input className='login-element' type="text" placeholder="Password" name ='Password' value={login_info.Password} onChange={handleChange}/>

      <button className='login-element' type="submit" onClick={handleSubmit}>Login</button>
    </form>
  </div>
  );
}

export default LoginPage;
