import { useState } from 'react';
import './app.css'
import 'axios'



function App() {

  const [login_info, setlogin_info] = useState({  
    name: '',
    surname: '',
    email: '',
    password: '',});

  const handleChange = (event)=>{
    const values = event.target.value;
    setlogin_info(val => ({...val, values}));
  }

  const handleSubmit = (event) => {
    event.preventDefault();

    alert (login_info);
  }


  return (
    <>
    <form>
      <label>Enter your name:
        <input type="text" value={login_info.name} onChange={handleChange}/>
      </label>

      <label>Enter your surname:
        <input type="text" value={login_info.surname } onChange={handleChange}/>
      </label>

      <label>Enter your email:
        <input type="email" value={login_info.email} onChange={handleChange}/>
      </label>

      <label>Enter your password:
        <input type="text" value={login_info.password} onChange={handleChange}/>
      </label>
      <button type="submit" onClick={handleSubmit}/>
    </form>
    </>
  );
}

export default App;
