import { useState } from 'react';
import '../app.css'
import axios from 'axios';




function LoginPage() {

  const [login_info, setlogin_info] = useState({  
    Name: '',
    Surname: '',
    Email: '',
    Password: '',});

  const handleChange = (event)=>{
    const { name, value } = event.target;
    setlogin_info((prevInfo) => ({ ...prevInfo, [name]: value }));
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    const result = await login();
    console.log(result)
    alert (login_info);
  }

  async function login() {
  try {
    const response = await axios.post('/login/Landlord', login_info);
    return response
  } catch (error) {
    console.log(error);
  }
}



  return (
  <div className='LoginPage'>


    <form className='Form'>


      <h1>Login</h1>

      <div>
      <p>Please select the type of user:</p>
      <input type="radio" id="Landlord" name="user" value="L"/>
      <label for="Landlord">Landlord</label>
      <input type="radio" id="Performer" name="user" value="P"/>
      <label for="Performer">Performer</label>
      <input type="radio" id="Spectator" name="user" value="S"/>
      <label for="Spectator">Spectator</label>
      </div>


    <input type="text" placeholder="your@email.com" name='Name' value={login_info.Name} onChange={handleChange}/>



    <input type="text" name='Surname' value={login_info.Surname } onChange={handleChange}/>



    <input type="email" name='Email' value={login_info.Email} onChange={handleChange}/>


    <input type="text" placeholder="Password" name ='Password' value={login_info.Password} onChange={handleChange}/>

      <button type="submit" className = 'Submit-btn' onClick={handleSubmit}>Login</button>
    </form>
  </div>
  );
}

export default LoginPage;
