import { useState } from 'react';
import './app.css'
import axios from 'axios';



function App() {

  const [login_info, setlogin_info] = useState({  
    name: '',
    surname: '',
    email: '',
    password: '',});

  const handleChange = (event)=>{
    const { name, value } = event.target;
    setlogin_info((prevInfo) => ({ ...prevInfo, [name]: value }));
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    await login();
    alert (login_info);
  }

  async function login() {
  try {
    const response = await axios.post('http://localhost:4000/login/Landlord', login_info);
    console.log(response);
  } catch (error) {
    console.error(error);
  }
}

  return (
    <>
    <form>
      <label>Enter your name:
        <input type="text" name='name' value={login_info.name} onChange={handleChange}/>
      </label>

      <label>Enter your surname:
        <input type="text" name='surname' value={login_info.surname } onChange={handleChange}/>
      </label>

      <label>Enter your email:
        <input type="email" name='email' value={login_info.email} onChange={handleChange}/>
      </label>

      <label>Enter your password:
        <input type="text" name ='password' value={login_info.password} onChange={handleChange}/>
      </label>
      <button type="submit" onClick={handleSubmit}/>
    </form>
    </>
  );
}

export default App;
