import { useState } from 'react';
import '../app.css'
import axios from 'axios';




function LoginPage() {

  const [selectedOption, setSelected_option] = useState('S');

  const handleOption_change = (event)=>{
    setSelected_option(event.target.value);

  }



  const [login_info, setlogin_info] = useState({  
    Name: '',
    Surname: '',
    Email: '',
    Password: '',});

  const handleChange = (event)=>{
    const { name, value } = event.target;
    setlogin_info((prevInfo) => ({ ...prevInfo, [name]: value }));
  }

  const handleSubmit = async (event) => {
    console.log(login_info)
    event.preventDefault();
    const result = await login();
    console.log(result)
    alert (login_info);
  }

  async function login() {
  try {
    const response = await axios.post('/login/Landlord', login_info);
    return response
  } catch (error) {
    console.log(error);
  }
}



  return (
  <div className='LoginPage'>

    <h1>Login</h1>

      <form className='Form'>
        <input className='login-element' type="email" placeholder="your@email.com" name='Email' value={login_info.Email} onChange={handleChange} />
        <input className='login-element' type="text" placeholder="Password" name='Password' value={login_info.Password} onChange={handleChange} />
        <button className='login-element' type="submit" onClick={handleSubmit}>Login</button>
      </form>



  </div>
  );
}

export default LoginPage;
